#!/bin/bash

package_file="package.json"

function create_vue_project {
    echo -e "\033[0;32mCreating a new Vue project...\033[0m"
    npm create vite@latest vue_setup -- --template vue
    mv vue_setup/* .
    rm -rf vue_setup
}

if [ -s "$package_file" ]; then
    yarn install 2>/dev/null >/dev/null
else
    create_vue_project
    yarn install 2>/dev/null >/dev/null
fi

