echo -e "\e[0m- \033[0;31mBy HellFive Osborn\033[0m"
echo

argumento="$@"
args_array=($argumento)
manager="yarn"

# Verificar se o primeiro argumento é "npm" ou "yarn"
if [[ "${args_array[0]}" == "npm" ]]; then
    manager="npm"
    unset 'args_array[0]'
    args_array=("${args_array[@]}")
fi

# Baixe o arquivo e substitua se já existir
curl -o node_modules/.bin/vuesan -fSL https://raw.githubusercontent.com/HellFiveOsborn/vue-docker/scripts/vuesan

# Dê permissão de execução ao arquivo
chmod +x node_modules/.bin/vuesan

# Verificar se o primeiro argumento é "dev" e se não há argumentos adicionais
if [ "${args_array[0]}" == "dev" ] && [ "${#args_array[@]}" -eq 1 ]; then
    yarn dev --host --port 5000
else
    bash -c "${manager} ${args_array[*]}"
fi
